    public static void reverseWorld(Avatar avatar) {
        bg.flip(false, true);
        for (TextureRegion p:platformTextures)
            p.flip(false, true);
        for (TextureRegion p:avatar.playerTextures)
            p.flip(false, true);
        for (Animation a : avatar.playerMoveR)
            for (TextureRegion frame:a.getKeyFrames())
                frame.flip(false, true);
        for (Animation a : avatar.playerMoveL)
            for (TextureRegion frame:a.getKeyFrames())
                frame.flip(false, true);
    }

    public static void dispose() {
        // We must dispose of the texture when we are finished.
        texture.dispose();
        font.dispose();
        shadow.dispose();
    }
